import React from "react";

class Rating extends React.Component {
  constructor(props) {
    super(props);
    this.GrayStar = "data:image/svg+xml;base64,";
    this.GoldStar = "data:image/svg+xml;utf8;base64,";
    this.state = {
      rateList: Array(5).fill(this.GrayStar)
    }
  }

  renderRating(goldStarValue) {
    let goldRateList = this.state.rateList.slice();

    if (goldRateList[goldStarValue] === this.GoldStar) {
      goldRateList = Array(5).fill(this.GrayStar);
    }

    goldRateList.forEach((v, totalStars) => {
      if (totalStars <= goldStarValue) {
        goldRateList[totalStars] = this.GoldStar
      }
    })

    this.setState({
      rateList: goldRateList
    })
  }

  renderRateList() {
    const rateList = [];

    this.state.rateList.forEach((v, goldStarValue) =>  
      rateList.push(
      <img 
        src = {this.state.rateList[goldStarValue]}
        key = {goldStarValue}
        alt = "rate"
        onClick = {() => this.renderRating(goldStarValue)}
      />)
    )

    return rateList;
  }

  render() {
    return (
      <div className = "rateList">{this.renderRateList()}</div>
    )
  }
}

export default Rating;
