import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import Data from "./data.json";
import Searcher from "./Searcher.js";
import TableSorter from "./TableSorter.js";
import CompanyList from "./CompanyList.js";

class ServiceFinder extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      companyData: Data,

      changedInfoBySearch: Data,
      changedInfoByCost: Data,
      changedInfoByPopularity: Data,
      changedInfoByRate: Data,

      options: {
        changedInfoBySearch: true,
        changedInfoByPopularity: false,
        changedInfoByCost: false,
        changedInfoByRate: false,
      }
    }
    this.updateState = this.updateState.bind(this)
  }

  updateState(state) {
    let resultState;
    if ("options" in state) {
      for (let key in state.options) {
        if (state.options[key]) {
          resultState = key;
        }
      }
    }
    this.setState({
      [resultState]: state[resultState],
      options: {...this.state.options, ...state.options}
    }) 
  }

  render() {
    let renderedData = "changedInfoBySearch"; 
    for (let isSwitched in this.state.options) {
      if (this.state.options[isSwitched]) {
        renderedData = isSwitched;
      }
    }    

    return (
      <div className = "founder-component"> 
        <div className ="search-container">
          <Searcher updateState = {this.updateState} data = {this.state}/>
        </div>
        <div className = "service-container">
          <TableSorter updateState = {this.updateState} data = {this.state}/>
          {console.log(this.state)}
          <CompanyList data = {this.state[renderedData]}/>
        </div>
      </div>
    )
  }
}

ReactDOM.render(<ServiceFinder />, document.getElementById("component-wrapper"))
